import React, { useState } from 'react';
import { FaBars } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import { AiFillGithub, AiFillInstagram, AiFillLinkedin } from 'react-icons/ai';
import { GiFire } from "react-icons/gi";

import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  }
  
   return (
    <nav className="navigation">
      <div className="header__main flex">
        <div className="header__portfolio-icon flex">
            <a style={{ display: 'flex', alignItems: 'center', color: 'white', marginBottom: '10px'}}>
                    <GiFire size="2.5rem" /> <span>Portfolio</span>
            </a>
        </div>
        <div className="header__nav-links hide flex">
          <ul className="hide flex">
            <li><a href="#projects"><p>Projects</p></a></li>
            <li><a href="#technologies"><p>Technologies</p></a></li>
            <li><a href="#about"><p>About</p></a></li> 
          </ul>
        </div>
        <div className="header__nav-socials hide flex">
          <div className="socials__github"><a href="https://github.com"><AiFillGithub size="3rem" /></a></div>
          <div className="socials__linkedin"><a href="https://github.com"><AiFillLinkedin size="3rem" /></a></div>
          <div className="socials__instagram"><a href="https://github.com"><AiFillInstagram size="3rem" /></a></div>
        </div>
      </div>

      {/* <div className="burger-menu" onClick={toggleMenu}>
        <FaBars />
        <div className="header__nav-links smallscreen flex">
          <ul className="flex">
            <li><a href="#projects"><p>Projects</p></a></li>
            <li><a href="#technologies"><p>Technologies</p></a></li>
            <li><a href="#about"><p>About</p></a></li> 
          </ul>
        </div>
        <div className="header__nav-socials flex">
          <div className="socials__github"><a href="https://github.com"><AiFillGithub size="3rem" /></a></div>
          <div className="socials__linkedin"><a href="https://github.com"><AiFillLinkedin size="3rem" /></a></div>
          <div className="socials__instagram"><a href="https://github.com"><AiFillInstagram size="3rem" /></a></div>
        </div>
      </div> */}

    </nav>
    );
}

export default Header;
